#include <iostream>
#include <string>

using namespace std;

class Solution {
public:
    string lastNonEmptyString(string input_string) {
        if (input_string.empty()) {
            return "";
        }
        for (char c : input_string) {
            if (c!='') {
                return c + input_string;
            }
        }
        return input_string;
    }
};

int main() {
    Solution solution;
    string input_string = "abcabcbb";
    cout << solution.lastNonEmptyString(input_string) << endl;
    return 0;
}

