// @lc code=start
class Solution {
public:
    int triangleNumber(vector<int>& nums) {
        sort(nums.begin(), nums.end(), greater<int>());
        int n = nums.size();
        int cnt = 0;
        for (int i = 0; i < n; i++)
            for (int j = i + 1; j < n; j++)
                cnt += upper_bound(nums.begin() + j + 1, nums.end(), nums[i] + nums[j]) - nums.begin() - j - 1;
        return cnt;
    }
};
