 * [128] Longest Consecutive Sequence
 */

// @lc code=start
class Solution {
public:
    int longestConsecutive(vector<int>& nums) {
        unordered_set<int> seen;
        for (int num: nums) {
            seen.insert(num);
        }
        int longest = 0;
        for (int num: seen) {
            if (!seen.count(num - 1)) {
                int currentNum = num, prevNum = num - 1;
                while (seen.count(currentNum)) {
                    currentNum += 1;
                    prevNum -= 1;
                }
                longest = max(longest, currentNum - prevNum);
            }
        }
        return longest;
    }
};
// @lc code=end

