import math
from typing import List

class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end = False

class Solution:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, num: int) -> None:
        node = self.root
        for digit in str(num):
            if digit not in node.children:
                node.children[digit] = TrieNode()
            node = node.children[digit]
        node.is_end = True

    def get_prefix(self, num: int) -> int:
        node = self.root
        prefix = 0
        for digit in str(num):
            if digit not in node.children:
                return prefix
            prefix += int(digit)
            node = node.children[digit]
        return prefix

    def search(self, num: int) -> bool:
        node = self.root
        for digit in str(num):
            if digit not in node.children:
                return False
            node = node.children[digit]
        return True

    def longestCommonPrefix(self, nums: List[int]) -> int:
        def findLCP(nums: List[int]) -> List[int]:
            prefix = self.get_prefix(nums[0])
            lcp = [0] * len(nums)
            for i in range(len(nums)):
                for j in range(i + 1, len(nums)):
                    if nums[i] == nums[j]:
                        lcp[i] = j
                    else:
                        break
            return lcp

        if not nums:
            return 0

        lcp = findLCP(nums)
        return math.gcd(*nums) // 2 + 1


