    bool isUnivalued(TreeNode* root) {
        return isUnivaluedHelper(root, 0);
    }

    bool isUnivaluedHelper(TreeNode* root, int val) {
        if (root == nullptr) return true;
        if (root->val!= val) return false;
        return isUnivaluedHelper(root->left, val) && isUnivaluedHelper(root->right, val);
    }
};
