        int[][] ans = new int[n][n];
        int count = 1;
        int top = 0;
        int right = n-1;
        int bottom = n-1;
        int left = 0;
        while(top <= bottom && left <= right)
        {
            for(int i = left ; i <= right ; i++)
            {
                ans[top][i] = count;
                count++;
            }
            top++;
            for(int i = top ; i <= bottom ; i++)
            {
