        public int sumOfPower(int[] nums, int k) {
            final int mod = 1e9 + 7;
            int n = nums.length;
            long[][][] dp = new long[n + 1][k + 1][n + 1];
            for (long[][] d : dp)
                for (long[] d1 : d)
                    Arrays.fill(d1, -1);

            return sumOfPower(nums, n, k, 1);
