            if (dp[n][k][j]!= -1)
                return dp[n][k][j];
            if (j == 1) {
                dp[n][k][j] = 1;
                return dp[n][k][j];
            }
            int len = n - j + 1;
            long sum = 0;
            for (int i = 1; i <= len; i++) {
                long res = sumOfPower(nums, len, k - i, j - 1);
                if (res!= 0) {
                    sum += res;
                    dp[n][k][j] = sum;
                }
            }
            return dp[n][k][j];
        }
    }

