    bool canPartitionKSubsets(vector<int>& nums, int k) {
        sort(nums.begin(), nums.end(), greater<int>());
        vector<int> used(nums.size());
        int sum = accumulate(nums.begin(), nums.end(), 0);

        if(sum % k || sum / k > nums[0]) return false;
        vector<int> target(k, sum / k);
        int sumTarget = accumulate(target.begin(), target.end(), 0);

        backtracking(nums, k, target, used, ans);
        return true;
    }

    void backtracking(vector<int>& nums, int k, vector<int>& target, vector<int>& used, vector<vector<int>>& ans) {
        if(k == 1) {
            for(int& u : used) u = 0;
            for(int& t : target) t = 0;
            for(int i = 0; i < nums.size(); i++) {
                if(used[i] == 1 && nums[i] == target[i]) {
                    ans.push_back({{i, nums[i]}});
                    return;
                }
            }
            return;
        }
        for(int i = 0; i < nums.size(); i++) {
            if(used[i] == 1) continue;
            used[i] = 1;
            target[k - 1] -= nums[i];
            backtracking(nums, k - 1, target, used, ans);
            used[i] = 0;
            target[k - 1] += nums[i];
        }
    }
