class Solution:
    def findTargetSumWays(self, nums: List[int], target: int) -> int:
        """
        Given a non-empty array of integers,
        find the number of total ways to
        select a subset of elements (if any)
        such that the sum of these
        elements is equal to a given value
        target.
        :param nums: input
        :param target: target
        :return: number
        """
        return self.dfs(nums, 0, 0, target)

    def dfs(self, nums, index, sum, target):
        if index == len(nums):
            if sum == target:
                return 1
            return 0
        if sum + nums[index] > target:
            return self.dfs(nums, index + 1, sum, target)
        return self.dfs(nums, index + 1, sum + nums[index], target) + self.dfs(nums, index + 1, sum, target)

