class Solution {
    public int minimumArrayLength(int[] v) {
        int n = v.length;
        Arrays.sort(v);
        int d = v[n - 1];
        int ans = 1;
        for (int i = 1; i < n && d >= v[i]; i++) {
            if (v[i] == d)
                ans++;
            else {
                if (d % (d / v[i]) == 0)
                    ans += 2;
                d = v[i];
            }
        }
        return ans;
    }
