class Solution {
    public int kength(int[] apples, int k) {
        Arrays.sort(apples);
        int ans = 1;
        for (int i = 0; i < apples.length; i++) {
            k -= apples[i];
            if (k < 0)
                return -1;
            else if (k == 0) {
                k = apples[i] - 1;
                ans++;
            }
            else if (k > 0 && i + 1 < apples.length && apples[i + 1]!= apples[i]) {
                k = apples[i] - 1;
                ans++;
            }
        }
        return ans;
    }
