class Solution {
    public int[] countOfPairs(int n, int x, int y) {
        int[] result = new int[n];
        int minVal = Math.min(x, y);
        int maxVal = Math.max(x, y);
        for (int i = 0; i < n; i++) {
            int sum = (i > minVal)? i - maxVal + 1 : 0;
            int diff = Math.min(sum, Math.abs(x - i) + Math.abs(y - i));
            result[diff - 1] += 2;
        }
        return result;
    }
