class Solution {
    public int[] Sort(int[] aa, int[] bb) {
        int len1 = aa.length;
        int len2 = bb.length;
        for(int i = 0; i < len1; i++){
            for(int j = 0; j < len1 - 1 - i; j++){
                if(aa[j] > aa[j + 1]){
                    int temp = aa[j];
                    aa[j] = aa[j + 1];
                    aa[j + 1] = temp;
                }
            }
        }

        for(int i = 0; i < len2; i++){
            for(int j = 0; j < len2 - 1 - i; j++){
                if(bb[j] > bb[j + 1]){
                    int temp = bb[j];
                    bb[j] = bb[j + 1];
                    bb[j + 1] = temp;
                }
            }
        }
        for(int i = 0; i < len1; i++){
            for(int j = 0; j < len2; j++){
                if(aa[i] == bb[j]){
                    aa[i] = bb[j];
                    break;
                }
            }
        }
        return aa;
    }
}
