public class EpicQuestion {

    private static class Solution {
        public ArrayList<ArrayList<Integer>> resultGrid(ArrayList<ArrayList<Integer>> v, int k) {
            int r = v.size(), c = v.get(0).size();
            HashMap<Pair<Integer, Integer>, Pair<Integer, Integer>> m = new HashMap<>();

            for (int i = 0; i < r - 2; i++) {
                for (int j = 0; j < c - 2; j++) {
                    int s = 0;
                    int f = 0;

                    for (int x = i; x < i + 3; x++) {
                        for (int y = j; y < j + 3; y++) {
                            s += v.get(x).get(y);

                            if (x + 1 < i + 3 && Math.abs(v.get(x).get(y) - v.get(x + 1).get(y)) > k) {
                                f = 1;
                                break;
                            }

                            if (y + 1 < j + 3 && Math.abs(v.get(x).get(y) - v.get(x).get(y + 1)) > k) {
                                f = 1;
                                break;
                            }
                        }
                        if (f == 1)
                            break;
                    }

                    if (f == 1)
                        continue;

                    s = s / 9;

                    for (int x = i; x < i + 3; x++) {
                        for (int y = j; y < j + 3; y++) {
                            Pair<Integer, Integer> key = new Pair<>(x, y);

                            if (m.containsKey(key)) {
                                Pair<Integer, Integer> p = m.get(key);
                                m.put(key, new Pair<>(p.getKey() + s, p.getValue() + 1));
                            } else {
                                m.put(key, new Pair<>(s, 1));
                            }
                        }
                    }
                }
            }

            ArrayList< ArrayList<Integer>> finalRes = new ArrayList<>();
            for (int i = 0; i < r; i++) {
                for (int j = 0; j < c; j++) {
                    finalRes.add(new ArrayList<>(m.get(new Pair<>(i, j))));
                }
            }

            return finalRes;
        }
    }

    private static class Pair<T, U> {
        private T key;
        private U value;

        public Pair(T key, U value) {
            this.key = key;
            this.value = value;
        }

        public T getKey() {
            return key;
        }

        public U getValue() {
            return value;
        }
    }

