        v.add(new ArrayList<>(Arrays.asList(1, 2, 3)));
        v.add(new ArrayList<>(Arrays.asList(4, 5, 6)));
        v.add(new ArrayList<>(Arrays.asList(7, 8, 9)));

        int k = 3;
        System.out.println(EpicQuestion.Solution.resultGrid(v, k));
    }
