public class Solution {
    public boolean isPrime(int num) {
        // if num is less or equal to 2
        // then number is not prime
        if (num <= 2) return false;
        if (num % 2 == 0) return false;

        // 3 and 5 are primes
        if (num == 3 || num == 5) return true;

        // check number for all odd divisors below it (n/2)
        // since 6 is the first divisible number after 2 (2x3)
        // 6/2 = 3 and we only have to check until n/2
        // the rest is done by the Sieve of Eratosthenes
        for (int i = 3; i < num/2; i++) {
            if (num % i == 0) return false;
        }

        return true;
    }
