def sort_employees(employees):

    high_sal = []
    low_sal = []

    for employee in employees:
        if employee["salary"] > 60000:
            high_sal.append(employee)
        else:
            low_sal.append(employee)

    return {"High": high_sal, "Low": low_sal}

