class Solution {
public:
    vector<int> shortestDistanceAfterQueries(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> ans(queries.size());
        for (int i = 0; i < n; ++i) {
            vis[i] = i;
            adj[i].push_back(i + 1);
        }

        for (int i = 0; i < queries.size(); ++i) {
            int x = queries[i][0], y = queries[i][1];
            nums[x] = y;
            adj[x].push_back(y);
            ans[i] = step(i);
        }
        return ans;
    }

private:
    vector<vector<int>> adj = vector<vector<int>>(10010);
    vector<int> vis = vector<int>(10010, -1);
    vector<int> dist = vector<int>(10010, 10010);

    int step(int k) {
        queue<int> q;
        q.push(0);
        dist[0] = 0;

        while (q.size()) {
            int curr = q.front();
            q.pop();

            for (int nb : adj[curr]) {
                if (dist[nb] == 10010) {
                    dist[nb] = dist[curr] + 1;
                    if (vis[nb] != k) {
                        vis[nb] = k;
                        q.push(nb);
                    }
                }
            }
        }

        int res = dist[n - 1];
        fill(dist.begin(), dist.end(), 10010);
        return res == 10010 ? -1 : res;
    }
};


