class Solution:
    def canAliceWin(self, nums: List[int]) -> bool:
        single=0
        double=0
        for it in nums:
            if it>=10: 
                double=double|1<<(it-10)
            else:
                single=single|1<<it

        if double==0 or single==0: return bool(single)

        single_can_win = [0] * 1<<10
        double_can_win = [0] * 1<<10
        for it in single:
            for x in double:
                if it|x not in single_can_win and it|x not in double_can_win:
                    if not double_can_win[x] and it not in single_can_win:
                        single_can_win[it|x] = True
                    else:
                        double_can_win[it|x] = True
        return single_can_win[0]

