class Graph:
    def __init__(self):
        self.graph = {}

    def add_edge(self, v1, v2, c):
        if v1 not in self.graph:
            self.graph[v1] = {}
        self.graph[v1][v2] = c

    # Floyd warshall's algorithm
    def floyd(self):
        n = len(self.graph)
        # Initializing dist[ ] with shortest possible edge weights.
        # All are considered as infinite initially except for
        # the nodes themselves for which it is zero.
        dist = self.graph.copy()
        for i in dist:
            for j in dist:
                if i == j:
                    dist[i][j] = 0
                else:
                    dist[i][j] = min(dist[i][j], dist[j][i])

        # Add all vertices one by one to the set of intermediate
        # vertices.
        # ---> Before start of a iteration, we have shortest
        #      distances between all pairs of vertices such that
        #      the shortest distances consider only the vertices in
        #      set {0, 1, 2, .. k-1} as intermediate vertices.
        # ----> After the end of a iteration, vertex no. k is added
        #       to the set of intermediate vertices and the set
        #       becomes {0, 1, 2, .. k}
        for k in range(n):
            for i in range(n):
                for j in range(n):
                    # Edge distances for new vertices is considered.
                    # If dist(i, j) > dist(i, k) + dist(k, j)
                    # Update distance of edge i, j
                    if dist[i][j] > dist[i][k] + dist[k][j]:
                        dist[i][j] = dist[i][k] + dist[k][j]

        return dist

    def __str__(self):
        return str(self.graph)


