    def findChampion(self, n: int, edges: List[List[int]]) -> int:

        graph = [[] for _ in range(n)]
        for edge in edges:
            graph[edge[0]].append(edge[1])
        for i in range(n):

            visited = [False] * n
            count = self.dfs(graph, i, visited)

