// these numbers that has a sum close to half of the total sum of the numbers.
// The answer is returned as an array consisting of a subset's sum and an
// array containing all the numbers in the subset.

// For example, given [7, 14, 2, 24], your code should return [33, [7, 2, 24]]
// since the subset [7, 2, 24] gives the maximum sum of 33.

// You can assume that no integer will be a zero.

vector<int> maxSum(const vector<int> &nums) {
    vector<int> sums;

    int maxSum = 0;
    for (auto n : nums)
    {
        maxSum += n;
        sums.push_back(maxSum);
    }

    int lastSum = *(find(sums.begin(), sums.end(), maxSum/2 + 1));
    vector<int> finalVec;

    int i = 0;
    while(lastSum > 0)
    {
        if (lastSum == sums[nums.size()-1])
        {
            finalVec.push_back(lastSum);
            finalVec.insert(finalVec.begin(), nums.begin(), nums.end());
            return finalVec;
        }
        i = lower_bound(sums.begin(), sums.end(), lastSum) - sums.begin();
        finalVec.push_back(lastSum);
        lastSum -= nums[i];
    }

    finalVec.insert(finalVec.begin(), nums.begin(), nums.end());

    return finalVec;
}

