class Solution {
    public int[] twoSum(int[] nums, int target) {
        Map<Integer, Integer> numMap = new HashMap<>();
        int n = nums.length;
        for(int i=0; i<n; i++){
            int complement = target-nums[i];
            if(numMap.containsKey(complement)){
                output[0] = numMap.get(complement);
                output[1] = i;
                break;
            }else{
                numMap.put(nums[i], i);
            }
        }
        return output;
    }
}
