class Solution {
    public int minimumTimeToInitialState(String word, int k) {
        StringBuilder sb = new StringBuilder();
        int count = 0;
        for(int i=0;i<word.length();i+=k) {
            sb.append(word,i,k);
        }
        return sb.toString().equals(word) ? count+1 : -1;
    }
}


