import java.util.LinkedList;
import java.util.Queue;

public class ChampionFinder {
    public static int findChampion(int n, int[][] edges) {
        // Create an array to store the degrees of each team
        int[] degrees = new int[n];
        // Create an array of LinkedLists to represent the graph of teams
        LinkedList<Integer>[] graph = new LinkedList[n];
        for (int i = 0; i < n; i++) {
            graph[i] = new LinkedList<>();
        }
        // Add the edges to the graph and increment the degree of the destination team
        for (int[] edge : edges) {
            graph[edge[0]].add(edge[1]);
            degrees[edge[1]]++;
        }
        // Create a queue to store the teams with zero degrees
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < n; i++) {
            if (degrees[i] == 0) {
                queue.offer(i);
            }
        }
        // Initialize the champion variable
        int champion = -1;
        // Process the teams in the queue until the queue is empty
        while (!queue.isEmpty()) {
            // If there are multiple teams with zero degrees, there is no unique champion
            if (queue.size() > 1) {
                return -1;
            }
            // Get the first team in the queue
            int team = queue.poll();
            // Set the team as the champion
            champion = team;
            // Decrement the degrees of the destination teams of the current team
            for (int dest : graph[team]) {
                degrees[dest]--;
                // If a destination team becomes zero degree, add it to the queue
                if (degrees[dest] == 0) {
                    queue.offer(dest);
                }
            }
        }
        // Return the champion
        return champion;
    }
}

