class Solution {
    public long minimumCost(int[] nums, int k, int dist) {
        int n = nums.length;
        long[] sum = new long[n + 1];
        for (int i = 0; i < n; ++i) {
            sum[i + 1] = sum[i] + nums[i];
        }
        long[][] dp = new long[n + 1][k + 1];
        Arrays.fill(dp[0], Long.MAX_VALUE);
        dp[0][0] = 0;
        for (int i = 0; i <= n; ++i) {
            for (int j = 0; j <= k; ++j) {
                if (dp[i][j] == Long.MAX_VALUE) {
                    continue;
                }
                if (j < k) {
                    for (int l = 0; l <= dist && i + l < n; ++l) {
                        long cur = dp[i][j] + sum[i + l + 1] - sum[i];
                        dp[i + l + 1][j + 1] = Math.min(dp[i + l + 1][j + 1], cur);
                    }
                }
                if (i < n) {
                    dp[i + 1][j] = Math.min(dp[i + 1][j], dp[i][j] + nums[i]);
                }
            }
        }
        return dp[n][k];
    }
}

