    vector<int> findThePrefixCommonArray(vector<int>& A, vector<int>& B) {
        vector<int> ans;
        long long p = 0, q = 0;
        for (int i = 0; i < A.size(); ++i) {
            p |= 1LL << A[i];
            q |= 1LL << B[i];
            int cnt = __builtin_popcountll(p & q);
            ans.push_back(cnt);
        }
        return ans;
    }
};



