class Graph {
public:
  typedef std::pair<int, int> pii;
  Graph(int n, std::vector<std::vector<int>>& edges) {
    this->graph = std::vector<std::vector<pii>>(n);
    for (auto &vec : edges) {
      int x = vec[0];
      int y = vec[1];
      int cost = vec[2];
      this->graph[x].emplace_back(y, cost);
    }
  }

  void addEdge(std::vector<int> edge) {
    int x = edge[0];
    int y = edge[1];
    int cost = edge[2];
    this->graph[x].emplace_back(y, cost);
  }

  int shortestPath(int node1, int node2) {
    std::priority_queue<pii, std::vector<pii>, std::greater<pii>> pq;
    std::vector<int> dist(this->graph.size(), INT_MAX);
    dist[node1] = 0;
    pq.emplace(0, node1);
    while (!pq.empty()) {
      auto [cost, cur] = pq.top();
      pq.pop();
      if (cur == node2) {
        return cost;
      }
      for (const auto& [next, ncost] : this->graph[cur]) {
        if (dist[next] > cost + ncost) {
          dist[next] = cost + ncost;
          pq.emplace(cost + ncost, next);
        }
      }
    }
    return -1;
  }

private:
  std::vector<std::vector<pii>> graph;
};

