public boolean canSortArray(int[] nums) {
    int lastCnt = 0;
    int lastGroupMax = 0;
    for (int num : nums) {
        int curCnt = Integer.bitCount(num);
        if (curCnt != lastCnt) {
            lastCnt = curCnt;
            lastGroupMax = num;
        } else {
            lastGroupMax = Math.max(num, lastGroupMax);
        }
        if (num < lastGroupMax) {
            return false;
        }
    }
    return true;
}

