public ListNode modifiedList(int[] nums, ListNode head) {
    // 使用哈希集合存储 nums 中的值
    Set<Integer> set = new HashSet<>(nums.length);
    for (int x : nums) {
        set.add(x);
    }
    // 创建一个虚拟头结点
    ListNode dummy = new ListNode(0, head);
    // 使用双指针法删除链表中的节点
    ListNode prev = dummy;
    ListNode curr = head;
    while (curr != null) {
        if (set.contains(curr.val)) {
            // 如果当前节点的值在哈希集合中，则删除该节点
            prev.next = curr.next;
        } else {
            // 否则，将 prev 指针移到当前节点，并移动 curr 指针
            prev = curr;
        }
        curr = curr.next;
    }
    // 返回虚拟头结点的下一个节点
    return dummy.next;
}

