def numberOfAlternatingGroups(colors: List[int], queries: List[List[int]]) -> List[int]:
    groups = [0] * len(colors)

    # Merge groups of alternating colors
    for i in range(len(colors) - 1):
        if colors[i] != colors[i + 1]:
            groups[i + 1] = groups[i]
        else:
            groups[i + 1] = groups[i] + 1

    # Count groups of each size
    group_counts = [0] * (max(groups) + 1)
    for group in groups:
        group_counts[group] += 1

    # Calculate answers for type 1 queries
    ans = []
    for q in queries:
        if q[0] == 1:
            size = q[1]
            ans.append(group_counts[size - 1])

    return ans

