class Solution {
    static int const MOD = 1000000007;

  public:
    int maximizeSquareArea(int m, int n, vector<int>& h, vector<int>& v) {
        auto hset = f(h, m);
        auto vset = f(v, n);

        int ans = 1;
        for (int i : hset) {
            int minDiff = INT_MAX;
            for (int j : vset) {
                int diff = abs(i - j);
                minDiff = min(minDiff, diff);
            }

            ans = max(ans, minDiff);
        }

        return ans * ans % MOD;
    }

    set<int> f(vector<int>& a, int mx) {
        a.emplace_back(1);
        a.emplace_back(mx);
        sort(begin(a), end(a));

        set<int> b;
        for (int i = 1; i < (int)a.size(); ++i)
            b.emplace(a[i] - a[i - 1]);

        return b;
    }
};
