// code by abhijeet kanase
class Solution {
  public:
    // Function to minimize the string value by replacing '?' characters
    string minimizeStringValue(string s) {
        int n = s.size();
        string ans = "";  // Initialize the resulting minimized string
        int A[26] = {0};  // Array to store the count of each lowercase English letter

        // Step 1: Count the occurrences of each lowercase English letter
        for (auto i : s) {
            if (i!= '?') {
                A[i - 'a']++;  // Increment the count for non-'?' characters
            }
        }

        string dum = "";  // String to store selected letters for '?' characters

        // Step 2: Iterate through the string's' to handle '?' characters
        for (int i = 0; i < n; i++) {
            if (s[i] == '?') {
                int min = 0, cnt = 0;  // Initialize minimum count and current count for '?' character
                for (int j = 0; j < 26; j++) {
                    if (A[j] > 0) {
                        cnt = A[j];  // Update current count
                        if (cnt < min) {
                            min = cnt;  // Update minimum count
                        }
                    }
                }
                if (min > 0) {  // If '?' character has minimum count
                    dum += min;  // Append the selected letter to the string
                    A[min]--;  // Decrement the count for the selected letter
                }
            } else {
                ans += s[i];  // Append non '?' characters to the result string
            }
        }
        // Append the remaining '?' characters to the result string
        while (A[0] > 0) {
            ans += "?";
            A[0]--;
        }
        return ans;
    }
};

