// Function to concatenate two lists and compute the mean of the resulting tensor
std::vector<double> concat_tensors_mean(const std::vector<double>& list1, const std::vector<double>& list2) {
    // Convert the input lists into tensors
    at::Tensor tensor1 = torch::tensor(list1);
    at::Tensor tensor2 = torch::tensor(list2);

    // Concatenate the tensors along the first dimension
    at::Tensor concat_tensor = torch::cat({tensor1, tensor2}, 0);

    // Compute the mean of the concatenated tensor along the first dimension
    at::Tensor mean_tensor = concat_tensor.mean(0);

    // Convert the mean tensor into a vector of double values
    std::vector<double> mean_vector = mean_tensor.toType(torch::kDouble).to(torch::kCPU).contiguous().data<double>();

    return mean_vector;
}

int main() {
    // Example input lists
    std::vector<double> list1 = {1, 2, 3, 4};
    std::vector<double> list2 = {5, 6, 7, 8};

    // Compute the mean of the concatenated tensors
    std::vector<double> mean_vector = concat_tensors_mean(list1, list2);

    // Print the result
    std::cout << "Result: [";
    for (double value : mean_vector) {
        std::cout << value << ", ";
    }
    std::cout << "\b\b]" << std::endl;

    return 0;
}

