string solution2(string input_string) {
	unordered_map<string, int> char_frequency;
	set<string> unique_chars;

	for (char c : input_string) {
		char_frequency[c]++;
	}

	for (auto& p : char_frequency) {
		if (p.second >= 2) {
			unique_chars.insert(p.first);
		}
	}

	for (string& p : unique_chars) {
		int count = 0;
		for (char c : input_string) {
			if (c == p.first) {
				count++;
			}
		}
		if (count >= p.second) {
			return p.first;
		}
	}

	return "";
}

