class Solution {
public:
    int sumOddLengthSubarrays(vector<int>& arr) {
        int n = arr.size(), result = 0;
        for(int i = 0; i < n; ++i){
            int low = 0, high = i;
            while(low < high){
                int mid = (low + high) / 2;
                if(i - mid - 1 < 0){
                    low = mid + 1;
                    continue; 
                }else{
                    result += (i - mid - 1) * arr[mid] + arr[mid];
                    low = mid + 1;
                }
            }
            result += (i - low) * arr[low];
        }
        return result;
    }
};  // Medium/1588.SumofAllOddLengthSubarrays.cpp
