class Solution {
public:
    int sumOddLengthSubarrays(std::vector<int>& nums) {
        int n = nums.size(), result = 0;
        for(int i = 0; i < n; ++i){
            int low = 0, high = i;
            while(low < high){
                int mid = (low + high) / 2;
                if(i - mid - 1 < 0){
                    low = mid + 1;
                    continue; 
                }else{
                    result += (i - mid - 1) * nums[mid] + nums[mid];
                    low = mid + 1;
                }
            }
            result += (i - low) * nums[low];
        }
        return result;
    }
};

int main() {
