class Solution {
public:
    int countDigits(int num) {
        int cnt=0;
        while(num) {
            cnt++;
            num/=10;
        }
        return cnt;
    }
    int numOfArrays(int n) {
        vector<int>dp(countDigits(n)+1,0);
        dp[1]=9;
        dp[2]=9*9;
        dp[3]=9*9*9;
        for(int i=4;i<=countDigits(n);i++) {
            dp[i]=dp[i-1]*9;
        }
        int cnt=0;
        for(int i=1;i<n;i++) {
            int n1=i;
            int cnt1=0;
            while(n1) {
                cnt1++;
                n1/=10;
            }
            if(cnt1==countDigits(i))
                cnt+=dp[cnt1-1];
        }
        cnt+=dp[countDigits(n)];
        return cnt;
    }
};

