Example 1:
Input: [2,2,3,2]
Output: 3

Example 2:
Input: [0,1,0,1,0,1,99]
Output: 99
*/

// Time Complexity : O(n), Space complexity : O(n)
// This code passes all the leetcode tests
class Solution {
public:
    int singleNumber(vector<int>& nums) {
        unordered_map<int, int> hash;
        for (int num : nums) {
            hash[num]++;
        }
        for (auto& pair : hash) {
            if (pair.second == 1) {
                return pair.first;
            }
        }
        return 0;
    }
};
