        while(pos < s.length()){
            if(s[pos] =='') pos++;
            else if(s[pos] >= '0' && s[pos] <= '9'){
                while(pos < s.length() && (s[pos] >= '0' && s[pos] <= '9'))
                    n = n * 10 + s[pos++] - '0';
                if(op == '-') n *= -1;
                else if(op!= '+') stk.push(stk.top() * n);
                else stk.push(n); 
                pos++; 
            }
            else op = s[pos++];
        }
        long res = 0;
