class Solution {
public:
    bool checkInclusion(string s1, string s2) {
        int len1=s1.size(),len2=s2.size();
        if(len1>len2){return false;}
        int alpha[26]={0};
        for(int i=0;i<len1;i++)
        {
            alpha[s1[i]-'a']++;
        }
        int start=0,end=0,num=0;
        while(end<len2)
        {
            if(alpha[s2[end]-'a']>0)
            {
                num++;
            }
            alpha[s2[end]-'a']--;
            end++;
            while(num==len1)
            {
                if(end-start==len1)
                    return true;
                alpha[s2[start]-'a']++;
                if(alpha[s2[start]-'a']>0)
                    num--;
                start++;
            }
        }
        return false;
    }
};
int main()
{
    Solution s;
    string s1,s2;
    while(cin>>s1>>s2)
    {
        if(s.checkInclusion(s1,s2))
            cout<<"True"<<endl;
        else
            cout<<"False"<<endl;
    }
}
