import java.util.*;

class Solution {
    public int[] searchRange(int[] nums, int target) {
        int left = -1;
        int right = -1;
        for(int i=0;i<nums.length;i++)
        {
            if(nums[i]==target)
            {
                left = i;
            }
        }
        if(left==-1)
        {
            return new int[]{-1,-1};
        }
        for(int i=left;i<nums.length;i++)
        {
            if(nums[i]==target)
            {
                right = i;
            }
        }
        return new int[]{left,right};
    }
}
