package com.code.dp;

/**
 * Knapsack Problem
 */
public class KnapSack {

    public static void main(String[] args) {
        int[] wt=new int[]{1,3,4,5};
        int[] val=new int[]{1,4,5,7};
        int w=7;
        int n=wt.length;
        System.out.println(knapSack(w,wt,val,n));
    }

    public static int knapSack(int w, int[] wt, int[] val, int n) {
        int[][] dp=new int[w+1][n+1];
        for(int i=0;i<=n;i++)
        {
            dp[0][i]=0;
        }
        for(int j=0;j<=w;j++)
        {
            dp[j][0]=0;
        }

        for(int i=1;i<=n;i++)
        {
            for(int j=1;j<=w;j++)
            {
                if(wt[i-1]<=j)
                {
                    dp[j][i]=Math.max(dp[j][i-1],dp[j-wt[i-1]][i-1]+val[i-1]);
                }
                else
                {
                    dp[j][i]=dp[j][i-1];
                }
            }
        }
        return dp[w][n];
    }
}
