class Solution {
public:
    int minimumCost(int * nums, int nums_len) {
        int first = nums[0];
        int min1 = INT32_MAX, min2 = INT32_MAX;

        for (int i = 1; i < nums_len; i++) {
            if (min1 > nums[i]) {
                min2 = min1;
                min1 = nums[i];
            } else if (min2 > nums[i]) {
                min2 = nums[i];
            }
        }
