            this.data = new int[initSize];
            this.size = initSize;
            this.head = 0;
            this.tail = 0;
        }

        public boolean isEmpty() {
            return this.head == this.tail;
        }

        public boolean isFull() {
            return (this.tail + 1) % this.size == this.head;
        }

        public void addFirst(int e) {
            if (isFull()) {
                this.resize(this.size * 2);
            }
            this.data[this.head] = e;
            this.head = (this.head - 1 + this.size) % this.size;
        }

        public void addLast(int e) {
            if (isFull()) {
                this.resize(this.size * 2);
            }
            this.data[this.tail] = e;
            this.tail = (this.tail + 1) % this.size;
        }

        public int getFirst() {
            return this.data[this.head];
        }

        public int getLast() {
            return this.data[(this.tail - 1 + this.size) % this.size];
        }

        public void removeFirst() {
            if (isEmpty()) {
                throw new IndexOutOfBoundsException();
            }
            this.data[this.head] = this.data[this.tail];
            this.head = (this.head + 1) % this.size;
        }

        public void removeLast() {
            if (isEmpty()) {
                throw new IndexOutOfBoundsException();
            }
            this.data[(this.tail - 1 + this.size) % this.size] = this.data[this.head];
            this.tail = (this.tail - 1 + this.size) % this.size;
        }

        public int size() {
            return this.size;
        }

        public Deque resize(int n) {
            int[] temp = new int[n];
            System.arraycopy(this.data, 0, temp, 0, this.size);
            this.data = temp;
            this.size = n;
            return this;
        }
    }

