function splitNumber(num) {
    const nums = [];
    const i = 0;
    while(i < num.length) {
        if(i + 3 >= num.length) {
            nums.push(num[i]);
            i++;
        } else {
            nums.push(num[i]);
            nums.push(num[i+1]);
            i += 2;
        }
    }
    return nums;
}

