from typing import List


class Solution:
    def getAverages(self, nums: List[int], k: int) -> List[int]:
        avg = []
        for i in range(k-1, len(nums)-k):
            sub_sum = sum(nums[i-k+1:i+1+1])
            sub_sum -= (nums[i] + nums[i+1])
            avg.append(sub_sum/(2*k))
        return avg



