# @lc code=start
class Solution:
    def uniquePairs(self, nums: List[int]) -> int:
        count = {}
        for num in nums:
            if num in count:
                count[num] += 1
            else:
                count[num] = 1
        pairs = 0
        for num, freq in count.items():
            pairs += freq*(freq-1)//2
        return pairs

