//Python
def decodeString(S: str, K: int) -> str:
    res, dp = [], []
    for c in S:
        if c.isdigit():
            dp0 = dp[-1] + int(c)
            dp.append(dp0)
        else:
            res.append(c)
    res = ''.join(res)
    return res[:K]

